import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { getDashboardSummary } from "features/cartSlice";

import Loading from "components/Loading";
import ErrorMessageBox from "components/ErrorMessageBox";
import { Row, Col, Card } from "react-bootstrap";
import Chart from "react-google-charts";

const AdminDashboard = () => {
  const { summary, error } = useSelector((state) => state.cart);
  let daily = summary.dailyOrders
  const dispatch = useDispatch();
  console.log(daily);
  useEffect(() => {
    dispatch(getDashboardSummary());
  }, [dispatch]);
  return (
    <div>
      <h1>Dashboard</h1>
      {summary.status === "pending" ? (
        <Loading />
      ) : error ? (
        <ErrorMessageBox variant="danger">{error}</ErrorMessageBox>
      ) : (
        <>
          <Row>
            <Col md={4}>
              <Card>
                <Card.Body>
                  <Card.Title>
                    {summary.users && summary.users[0]
                      ? summary.users[0].numUsers
                      : 0}
                  </Card.Title>
                  <Card.Text> Users</Card.Text>
                </Card.Body>
              </Card>
            </Col>
            <Col md={4}>
              <Card>
                <Card.Body>
                  <Card.Title>
                    {summary.orders && summary.users[0]
                      ? summary.orders[0].numOrders
                      : 0}
                  </Card.Title>
                  <Card.Text> Orders</Card.Text>
                </Card.Body>
              </Card>
            </Col>
            <Col md={4}>
              <Card>
                <Card.Body>
                  <Card.Title>
                    â‚¬
                    {summary.orders && summary.users[0]
                      ? summary.orders[0].totalSales.toFixed(2)
                      : 0}
                  </Card.Title>
                  <Card.Text> Orders</Card.Text>
                </Card.Body>
              </Card>
            </Col>
          </Row>
          <Row>
            <div className="my-3">
              <h2>Sales</h2>
              {daily.length === 0 ? (
                <ErrorMessageBox>No Sale</ErrorMessageBox>
              ) : (
                <Chart
                  width="100%"
                  height="400px"
                  chartType="BarChart"
                  loader={<div>Loading Chart...</div>}
                  data={[
                    ["Date", "Sales"],
                    ...daily.map((x) => [x._id, x.sales]),
                  ]}
                ></Chart>
              )}
            </div>
          </Row>

          {/*<div className="my-3">
		  <h2>Categories</h2>
		  {summary.productCategories?.length === 0 ? (
			<ErrorMessageBox>No Category</ErrorMessageBox>
		  ) : (
			<Chart
			  width="100%"
			  height="400px"
			  chartType="PieChart"
			  loader={<div>Loading Chart...</div>}
			  data={[
				['Category', 'Products'],
				...summary.productCategories.map((x) => [x._id, x.count]),
			  ]}
			></Chart>
		  )}
		</div>*/}
        </>
      )}
    </div>
  );
};

export default AdminDashboard;
