import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";
import { toast } from "react-toastify";

const initialState = {
  products: [],
  error: null,
  status: "",
  singleProduct: [],
  updatedProductData: [],
  updateData: {},
};

export const productsFetch = createAsyncThunk(
  "products/productsFetch",
  async () => {
    const result = await axios.get("/api/v1/products");
    return result?.data;
  }
);
export const productFetch = createAsyncThunk(
  "product/productFetch",
  async (id, { rejectWithValue }) => {
    try {
      const result = await axios.get("/api/v1/products/product/" + id);
      console.log(result.data);
      return result?.data;
    } catch (error) {
      return rejectWithValue(error.response.data);
    }
  }
);
export const adminRemoveProduct = createAsyncThunk(
  "product/deleteProduct",
  async ({ _id, toast }, { rejectWithValue }) => {
    try {
      const result = await axios.delete("/api/v1/products/" + _id);
      toast.success("Product Deleted Successfully");
      return result?.data;
    } catch (err) {
      return rejectWithValue(err.result.data);
    }
  }
);

export const adminCreateProduct = createAsyncThunk(
  "product/createProduct",
  async ({ navigate }, { rejectWithValue }) => {
    if (window.confirm("Are you sure to create?")) {
      try {
        const userLocal = JSON.parse(localStorage.getItem("user"));
        const result = await axios.post(
          "/api/v1/products",
          {},
          {
            headers: {
              authorization: `Bearer ${userLocal.token}`,
            },
          }
        );
        toast.success("product created successfully");
        navigate(`/admin/product/${result.data.product._id}`);
        return result?.data;
      } catch (error) {
        return rejectWithValue(error.result.data);
      }
    }
  }
);

export const adminUpdateProduct = createAsyncThunk(
  "product/adminUpdateProduct",
  async (id, { rejectWithValue }) => {
    try {
      const result = await axios.get("/api/v1/products/product/" + id);
      return result?.data;
    } catch (error) {
      return rejectWithValue(error.response.data);
    }
  }
);

export const updateProduct = createAsyncThunk(
  "product/updateProduct",
  async ({ navigate, updateData }, { rejectWithValue }) => {
    try {
      const userLocal = JSON.parse(localStorage.getItem("user"));
      const result = await axios.put(
        "/api/v1/products/" + updateData._id,
        updateData,
        {
          headers: { Authorization: `Bearer ${userLocal.token}` },
        }
      );

      navigate("/admin/products");
      return result?.data;
    } catch (error) {
      return rejectWithValue(error.response.data);
    }
  }
);

export const uploadFile = createAsyncThunk(
  "product/uploadFile",
  async (bodyFormData, { rejectWithValue }) => {
    try {
		const userLocal = JSON.parse(localStorage.getItem("user"));
		const result = await axios.post('/api/v1/upload', bodyFormData, {
			headers: {
			  'Content-Type': 'multipart/form-data',
			  authorization: `Bearer ${userLocal.token}`,
			},
		  });
		  	
		  toast.success('Image uploaded successfully');
		  return result?.data;
    } catch (error) {
      return rejectWithValue(error.response.data);
    }
  }
);

const productsSlice = createSlice({
  name: "products",
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(productsFetch.pending, (state) => {
        state.status = "pending";
      })
      .addCase(productsFetch.fulfilled, (state, action) => {
        state.status = "success";
        state.products = action.payload;
      })
      .addCase(productsFetch.rejected, (state, action) => {
        state.status = "rejected";
        state.error = "Data not loaded!!!";
      })
      .addCase(adminCreateProduct.pending, (state) => {
        state.status = "pending";
      })
      .addCase(adminCreateProduct.fulfilled, (state, action) => {
        state.status = "success";
        state.products = action.payload;
      })
      .addCase(adminCreateProduct.rejected, (state, action) => {
        state.status = "rejected";
        state.error = "Data not loaded!!!";
      })
      .addCase(productFetch.pending, (state) => {
        state.status = "pending";
      })
      .addCase(productFetch.fulfilled, (state, action) => {
        state.status = "success";
        state.singleProduct = action.payload;
      })
      .addCase(productFetch.rejected, (state, action) => {
        state.status = "rejected";
        state.error = action.payload.message;
      })
      .addCase(adminUpdateProduct.pending, (state) => {
        state.status = "pending";
      })
      .addCase(adminUpdateProduct.fulfilled, (state, action) => {
        state.status = "success";
        state.updatedProductData = action.payload;
      })
      .addCase(adminUpdateProduct.rejected, (state, action) => {
        state.status = "rejected";
        state.error = "Data not loaded!!!";
      })
      .addCase(updateProduct.pending, (state) => {
        state.status = "pending";
      })
      .addCase(updateProduct.fulfilled, (state, action) => {
        state.status = "success";
        state.updatedProductData = action.payload;
      })
      .addCase(updateProduct.rejected, (state, action) => {
        state.status = "rejected";
      })
      .addCase(adminRemoveProduct.pending, (state) => {
        state.status = "pending";
      })
      .addCase(adminRemoveProduct.fulfilled, (state, action) => {
        state.status = "success";
        const {
          arg: { id },
        } = action.meta;
        if (id) {
          state.userTours = state.userTours.filter((item) => item._id !== id);
          state.tours = state.tours.filter((item) => item._id !== id);
        }
      })
      .addCase(adminRemoveProduct.rejected, (state, action) => {
        state.status = "rejected";
        state.error = action.payload.message;
      });
  },
});

export default productsSlice.reducer;
