import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axios from "axios";
import { toast } from "react-toastify";
import { getError } from "utils";


const initialState = {
  cartItems: localStorage.getItem("cartItems")
    ? JSON.parse(localStorage.getItem("cartItems"))
    : [],

  paymentMethod: localStorage.getItem("paymentMethod")
    ? localStorage.getItem("paymentMethod")
    : {
        paymentMethodName: "",
        creditCard: {},
      },

  shippingAddress: localStorage.getItem("shippingAddress")
    ? JSON.parse(localStorage.getItem("shippingAddress"))
    : {},

  ordersSummary: localStorage.getItem("ordersSummary")
    ? JSON.parse(localStorage.getItem("ordersSummary"))
    : {},

	orderItems:[]
	
};

export const orderCart = createAsyncThunk("cart/orderCart",
async() => {
	const cartItems  = localStorage.getItem("cartItems")
	const shippingAddress  = localStorage.getItem("shippingAddress")
	const paymentMethod  = localStorage.getItem("paymentMethod")
	const ordersSummary = localStorage.getItem("ordersSummary")
	const userLocal = JSON.parse(localStorage.getItem("user"));

	try {
		const result = await axios.post("/api/v1/orders", 
		
		{
			orderItems: cartItems,
			shippingAddress: shippingAddress,
			paymentMethod: paymentMethod,
			ordersSummary: ordersSummary.totalPrice
		  },
		  {
			headers: {
			  authorization: `Bearer ${userLocal.token}`,
			},
		  });
		  return result?.data
	} catch (error) {
		toast.error(getError(error));
	}
})

const cartSlice = createSlice({
  name: "cart",
  initialState,
  reducers: {
    addToCart(state, action) {
      const itemIndex = state.cartItems.findIndex(
        (item) => item._id === action.payload._id
      );

      if (itemIndex >= 0) {
        state.cartItems[itemIndex].quantity += 1;
      } else {
        const tempProduct = { ...action.payload, quantity: 1 };
        state.cartItems.push(tempProduct);
      }
      localStorage.setItem("cartItems", JSON.stringify(state.cartItems));
    },

    increaseCart(state, action) {
      const itemIndex = state.cartItems.findIndex(
        (item) => item._id === action.payload._id
      );
      if (state.cartItems[itemIndex].quantity >= 1) {
        state.cartItems[itemIndex].quantity += 1;
      }
      localStorage.setItem("cartItems", JSON.stringify(state.cartItems));
    },
    decreaseCart(state, action) {
      const itemIndex = state.cartItems.findIndex(
        (item) => item._id === action.payload._id
      );
      if (state.cartItems[itemIndex].quantity > 1) {
        state.cartItems[itemIndex].quantity -= 1;
      }
      localStorage.setItem("cartItems", JSON.stringify(state.cartItems));
    },
    removeFromCart(state, action) {
      const newCartItems = state.cartItems.filter(
        (cartItem) => cartItem._id !== action.payload._id
      );
      state.cartItems = newCartItems;
      localStorage.setItem("cartItems", JSON.stringify(state.cartItems));
    },
    clearAllCart(state, action) {
      state.cartItems = [];
      localStorage.setItem("cartItems", JSON.stringify(state.cartItems));
    },
    saveShippingAddress(state, action) {
      state.shippingAddress = action.payload;
      localStorage.setItem(
        "shippingAddress",
        JSON.stringify(state.shippingAddress)
      );
    },
    deleteShippingAddress(state, action) {
      state.shippingAddress = {};
      localStorage.setItem(
        "shippingAddress",
        JSON.stringify(state.shippingAddress)
      );
    },
    savePaymentMethod(state, action) {
      state.paymentMethod = action.payload;
      localStorage.setItem(
        "paymentMethod",
        JSON.stringify(state.paymentMethod)
      );
    },
  },
  extraReducers: (builder) => {
	builder 
	.addCase()
  }
});

export const {
  addToCart,
  decreaseCart,
  increaseCart,
  removeFromCart,
  clearAllCart,
  saveShippingAddress,
  deleteShippingAddress,
  savePaymentMethod,
} = cartSlice.actions;
export default cartSlice.reducer;
