import { Helmet } from "react-helmet-async";
import { Form, Button } from "react-bootstrap";
import CheckoutSteps from "../components/CheckoutSteps";
import { useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";


const Payment = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const { shippingAddress, paymentMethod } = useSelector((state) => state.cart);
	const { user} = useSelector((state) => state.auth);

  const submitHandler = (e) => {
    e.preventDefault();
    navigate("/placeorder");
  };
  return (
    <div>
      <Helmet>
        <title>Payment</title>
      </Helmet>

      <CheckoutSteps step1 step2 step3></CheckoutSteps>
      <div className="container small-container">
        <h1 className="my-3">Payment</h1>
		
        <Form onSubmit={submitHandler}>
		<div className="mb-3">
            <Form.Check
              type="radio"
              id="PayPal"
              label="PayPal"
              value="PayPal"
              checked={'PayPal'}
            //  onChange={(e) => setPaymentMethod(e.target.value)}
            />
          </div>
          <div className="mb-3">
            <Form.Check
              type="radio"
              id="Stripe"
              label="Stripe"
              value="Stripe"
              checked={'Stripe'}
            //  onChange={(e) => setPaymentMethod(e.target.value)}
            />
          </div>
          <Form.Group className="mb-3" controlId="cardname">
            <Form.Label >Full Name</Form.Label>
            <Form.Control required type="text" id="cardname" name="cardname" placeholder="Full name on card"/>
          </Form.Group>
          <Form.Group className="mb-3" controlId="cardnumber">
            <Form.Label>Card Number</Form.Label>
            <Form.Control required  id="cardnumber" name="cardnumber" type='text' pattern='.{16}' placeholder="8888-8888-8888-8888" />
          </Form.Group>
          <Form.Group className="mb-3" controlId="expireddate">
            <Form.Label>Date</Form.Label>
            <Form.Control required type="text" id="expireddate" name="expireddate" placeholder="MM/YYYY"/>
          </Form.Group>
          <Form.Group className="mb-3" controlId="city">
            <Form.Label>CVV/CVC</Form.Label>
            <Form.Control required type="password" placeholder="***" />
          </Form.Group>

          <div className="mb-3">
            <Button variant="primary" type="submit">
              Continue
            </Button>
          </div>
        </Form>
      </div>
    </div>
  );
};

export default Payment;
