import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  cartItems: localStorage.getItem('cartItems') ? JSON.parse(localStorage.getItem('cartItems')) : [],

  shippingAddress: localStorage.getItem('shippingAddress') ? JSON.parse(localStorage.getItem('shippingAddress')) : {},

  };

const cartSlice = createSlice({
  name: "cart",
  initialState,
  reducers: {
    addToCart(state, action) {
      const itemIndex = state.cartItems.findIndex(
        (item) => item._id === action.payload._id
      );

      if (itemIndex >= 0) {
        state.cartItems[itemIndex].quantity += 1;
      } else {
        const tempProduct = { ...action.payload, quantity: 1 };
        state.cartItems.push(tempProduct);
      }
	  localStorage.setItem('cartItems', JSON.stringify(state.cartItems))
      // const tempCart = state.cartItems.reduce((a,c) => a + c.quantity,0)
      //  if(action.payload.countInStock < tempCart) {
      //	window.alert('Sorry! Product is out of stock')
      //  }
    },

    increaseCart(state, action) {
      const itemIndex = state.cartItems.findIndex(
        (item) => item._id === action.payload._id
      );
      if (state.cartItems[itemIndex].quantity >= 1) {
        state.cartItems[itemIndex].quantity += 1;
      }
	  localStorage.setItem('cartItems', JSON.stringify(state.cartItems))
    },
    decreaseCart(state, action) {
      const itemIndex = state.cartItems.findIndex(
        (item) => item._id === action.payload._id
      );
      if (state.cartItems[itemIndex].quantity > 1) {
        state.cartItems[itemIndex].quantity -= 1;
      }
	  localStorage.setItem('cartItems', JSON.stringify(state.cartItems))
    },
    removeFromCart(state, action) {
      const newCartItems = state.cartItems.filter((cartItem) => cartItem._id !== action.payload._id)
	  state.cartItems = newCartItems
	  localStorage.setItem('cartItems', JSON.stringify(state.cartItems))
      
    },
	clearAllCart(state, action) {
		state.cartItems = []	
		localStorage.setItem('cartItems', JSON.stringify(state.cartItems))
	},
	saveShippingAddress(state,action) {
		state.shippingAddress = action.payload
		localStorage.setItem('shippingAddress', JSON.stringify(state.shippingAddress))
	},
	deleteShippingAddress(state, action) {
		state.shippingAddress = {}
		localStorage.setItem('shippingAddress', JSON.stringify(state.shippingAddress))
	}
  },
}); 

export const { addToCart, decreaseCart, increaseCart, removeFromCart, clearAllCart, saveShippingAddress, deleteShippingAddress } = cartSlice.actions;
export default cartSlice.reducer;
